#!/bin/bash

# This script remaps the NODEGRUE_DYLD_LIBRARY_PATH variable as
# DYLD_LIBRARY_PATH for testing, to get around el capitan's new "security"
# features.

export DYLD_LIBRARY_PATH="$NODEGRUE_DYLD_LIBRARY_PATH"

source ~/.bash_profile
echo "NVM versions: `nvm ls`"
echo "Node.js exe is: `which node` (`node --version`)"
echo "PATH is: $PATH"

nvm use --delete-prefix v6.5.0 --silent

echo "Setting library path for OSX:"
echo $DYLD_LIBRARY_PATH
echo

echo "Executing: "
echo $@

$@
