#!/usr/bin/python

import run_python_34
run_python_34.bootstrap_v34()

import os
import sys
import argparse

import tkinter, tkinter.constants, tkinter.filedialog, tkinter.messagebox

import pygrue
import unpackage_makerbot_file

DESCRIPTION = \
"""
Simple TK GUI tool for unpackaging a .makerbot file.
"""

if __name__ == "__main__":

    parser = argparse.ArgumentParser(description=DESCRIPTION)

    root = tkinter.Tk()
    root.withdraw() # hide root window

    makerbot_filename = tkinter.filedialog.askopenfilename(title="Select a .makerbot file to unpackage...")

    try:                                                                        
        unpackage_makerbot_file.unpackage_makerbot(makerbot_filename)
    except Exception as e:                                                      
        tkinter.messagebox.showinfo("Unpackaging Failed!", "%s" % e)    
     
