/*
This file is just a little helper. It brings the Loop object from
geomutils, and brings it into the mgl namespace.  It also provides a few
typedefs that we find useful.
*/

#ifndef SRC_GUL_LOOP_H_
#define SRC_GUL_LOOP_H_

#include <list>

#include "geomutils/Loop.h"

// include these files as a geomutils slash loop dot h uses the geomutils
// versions.
#include "gul/Segment2Type.h"
#include "gul/Point2Type.h"

namespace mgl {
typedef geomutils::Loop Loop;
typedef std::list<Loop> LoopList;
}

#endif  // SRC_GUL_LOOP_H_
