/*
This file is just a little helper. It brings the OpenPath object from
geomutils, and brings it into the mgl namespace.  It also provides a few
typedefs that we find useful.
*/

#ifndef SRC_GUL_OPENPATH_H_
#define SRC_GUL_OPENPATH_H_

#include <list>
#include <vector>

#include "geomutils/OpenPath.h"

// LineSegment2 is used by OpenPath, so  we include its mgl version here.
#include "gul/Segment2Type.h"

namespace mgl {
typedef geomutils::OpenPath OpenPath;
typedef std::list<OpenPath> OpenPathList;
typedef std::vector<OpenPath> OpenPathVector;
}

#endif  // SRC_GUL_OPENPATH_H_
