/*
This file is just a little helper. It brings the Point2Type  object from
geomutils, and brings it into the mgl namespace. It also provides a few typedefs
that we find useful.
*/

#ifndef SRC_GUL_POINT2TYPE_H_
#define SRC_GUL_POINT2TYPE_H_

#include <vector>
#include <deque>

#include "geomutils/Vector2.h"

namespace mgl {
typedef geomutils::Vector2 Point2Type;
typedef std::vector<Point2Type> PointVector;
typedef std::deque<Point2Type> PointDeque;
}

#endif  // SRC_GUL_POINT2TYPE_H_
