/*
This file is just a little helper. It brings the LineSegment2 object from
geomutils, and brings it into the mgl namespace. It also provides a few typedefs
that we find useful.
*/

#ifndef SRC_GUL_SEGMENT2TYPE_H_
#define SRC_GUL_SEGMENT2TYPE_H_

#include <vector>

#include "geomutils/LineSegment2.h"

namespace geomutils {
typedef LineSegment2 Segment2;
}

namespace mgl {
typedef geomutils::LineSegment2 Segment2Type;
typedef std::vector<Segment2Type> SegmentVector;
typedef std::vector<std::vector<Segment2Type> > SegmentTable;
}

#endif  // SRC_GUL_SEGMENT2TYPE_H_

