#ifndef INCLUDE_THING_FILE_ID_H_
#define INCLUDE_THING_FILE_ID_H_

#include "mbcoreutils/tagged_id.h"

namespace Thingiverse {
namespace internal {
class FileIDPolicy {
 public:
  typedef unsigned int ValueType;

  static ValueType invalidValue() {
    return 0;
  }

  static bool valid(const ValueType value) {
    return value > 0;
  }
};
}

#define NOTHING

CREATE_TAGGED_ID_TYPE(FileID, internal::FileIDPolicy, NOTHING);
}

#endif  // INCLUDE_THING_FILE_ID_H_
